import React, { useEffect, useState } from 'react';
import PageHeader from '../../../components/PageHeader/PageHeader';
import Sale from '../../../images/sale.png';
import DefaultButton from '../../../components/DefaultButton/DefaultButton';
import styles from './CreateOffer.css';
import { createNewOffer } from '../../../actions/offersAction';
import { useNavigate } from 'react-router-dom';
import { FooterContainer } from '../../../containers/footer';

const Edit_Offer = () => {

    const [userProfile, setUserProfile] = useState(null);
    const [initialized, setInitialized] = useState(false);

    useEffect(() => {
        if (!userProfile) {
            setUserProfile(JSON.parse(window.localStorage.userProfile));
        }

        if (userProfile) {
            // We have everything initialized
            console.log({userProfile})
            setInitialized(true);
        }
    }, [userProfile, window.localStorage.userProfile]);

    const onCreate = async () => {
        const data = {
            title: document.getElementById("offerName").value,
            businessId: JSON.parse(window.localStorage.userProfile).businessId,
            startDate: document.getElementById("startDate").value,
            endDate: document.getElementById("endDate").value,
            description: document.getElementById("promoType").value,
            offerCode: document.getElementById("couponCode").value

        };

        const createOffer = await createNewOffer(data);

    };


    return initialized ? (
        <div>
            <PageHeader/>
            <div className='below-header'>
                <div className='info-container'>
                    <div className='=info-title'>
                        <label className='title'>
                            Create Offer
                        </label>
                        <hr className="horizontal-line"></hr>
                    </div>
                        <div className='label-input-container'>
                            <label className='input-label'>
                                Offer Name
                            </label>
                            <input className='input-field' type="text" id="offerName" />
                        </div>
                        {/* <div className='label-input-container'>
                            <label className='input-label'>
                                Business ID
                            </label>
                            <input className='input-field' type="text" id="businessId" />
                        </div> */}
                        <div className='label-input-container'>
                            <label className='input-label'>
                                Start Date
                            </label>
                            <input className='input-field' type="text" id="startDate" />
                        </div>
                        <div className='label-input-container'>
                            <label className='input-label'>
                                End Date
                            </label>
                            <input className='input-field' type="text" id="endDate" />
                        </div>
                        <div className='label-input-container'>
                            <label className='input-label'>
                                Promotion Type
                            </label>
                            <input className='input-field' type="text" id="promoType" />
                        </div>
                        <div className='label-input-container'>
                            <label className='input-label'>
                                Coupon Code
                            </label>
                            <input className='input-field' type="text" id="couponCode" />
                        </div>
                        <div className='offer-availibility'>
                            <label className='input-label'>
                                Availibility
                            </label>
                            <div className='drop-down-box'>
                                <select>
                                <option value="online">Online</option>
                                <option value="inStore">In Store</option>
                                <option value="allStores">All Stores</option>
                            </select>
                            </div>
                        </div>
                        <DefaultButton
                    text={'Create'}
                    onPress={onCreate}
                    disabled={false}/>
                    </div>
                    <div className='Sale'>
                    <img src={Sale}/>
                </div>
            </div>
            <FooterContainer />
        </div>
    ) : <></>;
};

export default Edit_Offer;
