import { memo, useEffect, useState } from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';

const EmbeddedMap = () => {
	const [initialized, setInitialized] = useState(false);
	const [center, setCenter] = useState(null);

	useEffect(() => {
		// Set latitude and longitude for map when this component mounts
		navigator.geolocation.getCurrentPosition((position) => {
			setCenter({ lat: position.coords.latitude, lng: position.coords.longitude });
		});

		// Everything is initialized so we are good to display the map
		setInitialized(true);
	}, []);
	
	const { isLoaded } = useJsApiLoader({
		id: 'google-map-script',
		googleMapsApiKey: "AIzaSyCoJ2dosqQree0ARyQ5Isa9XPUt_6CcoRg",
	});

	const containerStyle = {
		width: '100%',
		height: '100%',
	};

	return (
		isLoaded && initialized ? (
			<GoogleMap
				mapContainerStyle={containerStyle}
				center={center}
				zoom={15}
			>
				<Marker />
			</GoogleMap>
		) : <></>
	);
};

export default memo(EmbeddedMap);
