import React, { useEffect, useState } from 'react';
import PageHeader from '../../../components/PageHeader/PageHeader';
import DefaultButton from '../../../components/DefaultButton/DefaultButton';
import styles from './CreateOffer.css';
import { createNewOffer } from '../../../actions/offersAction';
import { useNavigate } from 'react-router-dom';
import { FooterContainer } from '../../../containers/footer';

const Edit_Offer = () => {

    const [userProfile, setUserProfile] = useState(null);
    const [initialized, setInitialized] = useState(false);

    const business = JSON.parse(localStorage.getItem('userProfile'));


    useEffect(() => {
        if (!userProfile) {
            setUserProfile(business);
        }

        if (userProfile) {
            // We have everything initialized
            console.log({userProfile})
            setInitialized(true);
        }
    }, [userProfile, business]);

    const onCreate = async () => {
        const data = {
            title: document.getElementById("offerName").value,
            businessId: business.id,
            startDate: document.getElementById("start").value,
            endDate: document.getElementById("end").value,
            description: document.getElementById("promoType").value,
            offerCode: document.getElementById("couponCode").value

        };

        const createOffer = await createNewOffer(data);
        console.log("success: ", createOffer)

    };


    return initialized ? (
        <div>
            <PageHeader/>
            <div className='below-header'>
                <div className='info-container'>
                    <div className='=info-title'>
                        <label className='title'>
                            Create Offer
                        </label>
                        <hr className="horizontal-line"></hr>
                    </div>
                        <div className='label-input-container'>
                            <label className='input-label'>
                                Name
                            </label>
                            <input className='input-field' type="text" id="offerName" required/>
                        </div>
                        <div className='label-input-container'>
                            <label className='input-label'>
                                Start Date
                            </label>
                            <input type="date" id="start" name="trip-start" min="2022-01-01" max="2025-12-31" required/>
                        </div>
                        <div className='label-input-container'>
                            <label className='input-label'>
                                End Date
                            </label>
                            <input type="date" id="end" name="trip-start" min="2022-01-01" max="2025-12-31" required/>
                        </div>
                        <div className='label-input-container'>
                            <label className='input-label'>
                                Description
                            </label>
                            <input className='input-field' type="text" id="promoType" required/>
                        </div>
                        <div className='label-input-container'>
                            <label className='input-label'>
                                Coupon Code
                            </label>
                            <input className='input-field' type="text" id="couponCode" required/>
                        </div>
                        {/* <div className='offer-availibility'>
                            <label className='input-label'>
                                Availibility
                            </label>
                            <div className='drop-down-box'>
                                <select>
                                <option value="online">Online</option>
                                <option value="inStore">In Store</option>
                                <option value="allStores">All Stores</option>
                            </select>
                            </div>
                        </div> */}
                        <DefaultButton
                    text={'Create'}
                    onPress={onCreate}
                    disabled={false}/>
                    </div>
            </div>
            <FooterContainer />
        </div>
    ) : <></>;
};

export default Edit_Offer;
