import React, { useState, useEffect } from "react";
import AllyOffersLogo from "../AllyOffersLogo/AllyOffersLogo";
import IconBubbleButton from "../IconBubbleButton/IconBubbleButton";
import MapIconButton from "../MapBubbleButton/MapBubbleButton";
import LogoutBubbleButton from "../LogoutBubbleButton/LogoutBubbleButton";
import { useNavigate } from 'react-router-dom';
import styles from './PageHeader.css';

const PageHeader = () => {
    const [showUserButtons, setShowUserButtons] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        setShowUserButtons(localStorage.getItem("loggedIn"));
    }), [localStorage.getItem("loggedIn")];

    console.log(showUserButtons)

    return (
        <div className='header'>
            <div onClick={()=> navigate('/offers')}>
                <AllyOffersLogo/>
            </div>
            <div className='bubble-buttons'>
                <MapIconButton/>
                {showUserButtons && <IconBubbleButton/>}
                {showUserButtons && <LogoutBubbleButton/>}
            </div>
        </div>
    );
};

export default PageHeader;
