import PageHeader from '../../../components/PageHeader/PageHeader';
import axios from 'axios';
import EmbeddedCalendar from "../../../components/EmbeddedCalendar";
import { getDashboard, getTable } from '../../../actions/businessDasboardAction';
import { json, useNavigate } from 'react-router-dom';
import styles from './Calendar.css';
import { FooterContainer } from '../../../containers/footer';
import { useEffect, useState } from 'react';


const calendar = () => {

    const navigate = useNavigate();

    const [userProfile, setUserProfile] = useState(null);
    const [initialized, setInitialized] = useState(false);
    const [dashboardTotals, setData] = useState(null);
    const [tableOffers, setTable] = useState(null);
    const [tableList, setTableList] = useState(null);
    const [graphList, setGraphList] = useState(null);



    useEffect(() => {
        if (!userProfile) {
            setUserProfile(JSON.parse(window.localStorage.userProfile));
        }

        if (userProfile && dashboardTotals && tableOffers) {
            // We have everything initialized
            setInitialized(true);
            console.log({dashboardTotals})
            console.log({tableOffers})
        }

        const dashboard = async() => {
            const businessId = JSON.parse(window.localStorage.userProfile).id
            const dashboardInfo = await getDashboard(businessId);
            console.log("dashboardInfo: ", dashboardInfo);
            setData(JSON.parse(dashboardInfo));

        }

        const table = async() => {
            const businessId = JSON.parse(window.localStorage.userProfile).id
            const tableInfo = await getTable(businessId);
            console.log("tableInfo: ", tableInfo);
            setTable(JSON.parse(tableInfo));
        }


        if(!dashboardTotals){
            dashboard();
        }

        if(!tableOffers){
            table();
        }

        if(!tableList && tableOffers){

            var tableLst = []

            for (var i = 0; i < tableOffers.length; i++){
                var row = <tr  className="odd:bg-white even:bg-gray-100 hover:bg-gray-200">
                                   <td className="px-6 py-4">{tableOffers[i].title}</td>
                                <td className="px-6 py-4">{tableOffers[i].offerCode}</td>
                                <td className="px-6 py-4">{tableOffers[i].description}</td>
                                <td className="px-6 py-4">{(new Date(tableOffers[i].startDate)).toDateString()}</td>
                                <td className="px-6 py-4">{(new Date(tableOffers[i].endDate)).toDateString()}</td>
                                <td className="px-6 py-4">{tableOffers[i].clips}</td>
                                <td className="px-6 py-4">{tableOffers[i].redemptions}</td>
                          </tr>
                tableLst.push(row)
            }   

            setTableList(tableLst);
        }


        if(!graphList && tableOffers){

            var graphLst = []

            for (var i = 0; i < tableOffers.length; i++){
                var row = <div className="relative flex flex-col items-center flex-grow pb-5 group w-16">
                                <span className="absolute top-0 -mt-6 text-xs font-bold group-hover:block">{tableOffers[i].redemptions}</span>
                                <div className="flex items-end w-8 pb-5">
                                    <div className="relative flex justify-center flex-grow h-20 bg-purple-400"></div>	
                                </div>
                                <span className="absolute bottom-0 text-xs font-bold">{tableOffers[i].title}</span>
                            </div>
                graphLst.push(row)
            }   

            setGraphList(graphLst);
        }

        
    }, [initialized, userProfile, window.localStorage.userProfile, dashboardTotals, tableOffers]);
                    

    

    return initialized ? (
        <div>
            <PageHeader/>
            <div className="outline outline-2 outline-gray-200 rounded-lg px-2 mx-16">
                <div className=" space-x-6 flex items-center  justify-around p-4  rounded-lg bg-white shadow-indigo-50 shadow-md">
                    <h3 className="flex items-left text-left text-gray-800 py-8 ml-2 text-4xl font-bold font-small leading-tight">{userProfile.businessName}</h3>
                    <div className="pl-4 py-16 mx-auto w-fit">
                        <div className="grid grid-cols-2 row-gap-8 md:grid-cols-3">
                            <div className="text-center px-3">
                                <h6 className="text-4xl font-bold text-purple-500 py-2">{dashboardTotals.savings_total}</h6>
                                <p className="text-sm font-medium tracking-normal text-gray-800 uppercase lg:text-base">
                                    Offers Saved
                                </p>
                            </div>
                            <div className="text-center px-3">
                                <h6 className="text-4xl font-bold text-purple-500 py-2">{dashboardTotals.redemptions_total}</h6>
                                <p className="text-sm font-medium tracking-normal text-gray-800 uppercase lg:text-base">
                                    Offers Redeemed
                                </p>
                            </div>
                            <div className="text-center px-3">
                                <h6 className="text-4xl font-bold text-purple-500 py-2">{dashboardTotals.offers_total}</h6>
                                <p className="text-sm font-medium tracking-normal text-gray-800 uppercase lg:text-base">
                                    Offers released
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>


            <div align="center">
            <button className='create hover:bg-violet-500' onClick={()=> navigate('/createoffer')}>Create Offer</button>
            <button className='create hover:bg-violet-500 hover:scale-105' onClick={()=> navigate('/editoffer')}>Edit Offer</button>


            <div className='flex items-center justify-between pt-2 w-full px-20'>
                <h2 className="justify-font-medium text-xl text-gray-900 pl-20 font-bold font-small">Offers List</h2>
            </div>
            <div className="relative overflow-x-auto shadow-md sm:rounded-lg w-fit flex justify-center">
                <table className=" text-sm text-left">
                    <thead className="text-xs text-black uppercase bg-gray-50 dark:text-gray-600">
                        <tr>
                            <th scope="col" className="px-6 py-3">
                                Name
                            </th>
                            <th scope="col" className="px-6 py-3">
                                Code
                            </th>
                            <th scope="col" className="px-6 py-3">
                                Description
                            </th>
                            <th scope="col" className="px-6 py-3">
                                Start Date
                            </th>
                            <th scope="col" className="px-6 py-3">
                                End Date
                            </th>
                            <th scope="col" className="px-6 py-3">
                                Saved
                            </th>
                            <th scope="col" className="px-6 py-3">
                                Redeemed
                            </th>
                        </tr>
                    </thead>

                    <tbody id="table">
                        {tableList}
                    </tbody>

                </table>
            </div>
            </div>
            

            <div align="center">
                <div className='flex items-center justify-between pt-2 w-full px-20'>
                    <h2 className="justify-font-medium text-xl text-gray-900 pl-20 font-bold font-small">Business Analysis</h2>
                </div>

                <div className="flex flex-col items-center w-4/5 mt-5 bg-white rounded-lg shadow-lg px-5 py-5">
                    <h2 className="text-xl font-bold pb-5">Redeem Count</h2>
                    <div className="flex items-end flex-grow w-fit mt-2 pt-4 mx-3">
                        {graphList}
                    </div>
                </div>
            </div>

            <div className=''>

            </div>
            <FooterContainer />
        </div>

        
        
        
    ) : <></>;
};

export default calendar;
