import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from 'react-router-dom';
import PageHeader from "../../../components/PageHeader/PageHeader";
import { FooterContainer } from "../../../containers/footer";
import { getOfferDetails } from "../../../actions/offersAction";
import styles from './SavedOffers.css';

const SavedOffers = () => {
    const navigate = useNavigate();

    const userOffers = JSON.parse(localStorage.getItem('userProfile')).offers;

    const [savedOffers, setSavedOffers] = useState(null);
    const [redeemedOffers, setRedeemedOffers] = useState(null);

    useEffect(() => {
        const loadAllOffers = async () => {
            const savedOffersDetails = await Promise.all(userOffers.saved.map(async (offerID) => {
                const offerDetails = await getOfferDetails(offerID);
                return offerDetails;
            }));
            const redeemedOffersDetails = await Promise.all(userOffers.redeemed.map(async (offerID) => {
                const offerDetails = await getOfferDetails(offerID);
                return offerDetails;
            }));
            console.log(savedOffersDetails)
            console.log(redeemedOffersDetails)
            setSavedOffers(savedOffersDetails);
            setRedeemedOffers(redeemedOffersDetails);
        };
        loadAllOffers();
    }, []);

    const renderCards = (offerDetailsArray) => {
        return (
            offerDetailsArray.map((offer) => {
                const startDate = (new Date(offer.startDate)).toDateString();
                const endDate = (new Date(offer.endDate)).toDateString();
                return (
                    <button className="saved-offers-card" >
                        <div className="saved-offers-card-inner-container">
                            <h5 className="saved-offers-card-title">{offer.title}</h5>
                            <div className="saved-offers-card-dates">
                                <h5>Available: {startDate}</h5>
                                <h5>Expires: {endDate}</h5>
                            </div>
                        </div>
                    </button>
                );
            })
        );
    };

    return (
        <div>
            <PageHeader/>
            <div className="saved-offers-title">
                Your saved offers
            </div>
            {savedOffers && redeemedOffers && (
                <div className="saved-offers-containter">
                    <div className="saved-offers-subtitle ">Saved</div>
                    <div className="saved-offers-offers-container">
                        {renderCards(savedOffers)}
                    </div>
                    <div className="saved-offers-subtitle ">Redeemed</div>
                    <div className="saved-offers-offers-container">
                        {renderCards(redeemedOffers)}
                    </div>
                </div>
            )}
            <FooterContainer/>
        </div>
    );
};

export default SavedOffers;
