import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from 'react-router-dom';
import PageHeader from '../../../components/PageHeader/PageHeader';
import { FooterContainer } from '../../../containers/footer';
import { getBusinessDetails } from "../../../actions/customerAction";
import styles from './Storefront.css';

const Storefront = () => {
    const navigate = useNavigate();

    const params = useLocation();
    const businessID = params.state.businessID;
    console.log('Business ID:', businessID)
    const [businessDetails, setBusinessDetails] = useState(null);

    useEffect(() => {
        const loadBusinessDetails = async () => {
            const businessInfo = await getBusinessDetails(businessID);
            console.log('Business info:', businessInfo)
            setBusinessDetails(businessInfo);
        };
        loadBusinessDetails();
    }, []);

    return (
        <div>
            <PageHeader/>
            <h3 className="text-center bg-gray-50 text-gray-800 py-20 text-3xl font-bold font-medium leading-tight text-5xl">Store name</h3>
            <div>
                The store's details including name, category, address, and offers will be shown on this page for the customer to see.
            </div>
            <FooterContainer/>
        </div>
    );
};

export default Storefront;
