import axios from 'axios';

export const onCreate = async (offerInfo) => {


};

// Create a new offer
export const createNewOffer = async (offerInfo) => {
    const data = {
        title: offerInfo.title,
        businessId: offerInfo.businessId,
        startDate:  offerInfo.startDate,
        endDate:  offerInfo.endDate,
        description:  offerInfo.description,
        offerCode:  offerInfo.offerCode

    }

    const response = await axios.post(
        "http://localhost:8080/BusinessUsers/CreateOffer",
        data,
        {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
            },
        },
    )
    .catch((error) => {
        if (error.response) {
            console.log('Server responded with status code:', error.response.status);
            console.log('Response data:', error.response.data);
        } else if (error.request) {
            console.log('No response received:', error.request);
        } else {
            console.log('Error creating request:', error.message);
        }
    });
    return await response.data;
};


// Update an offer
export const updateOffer = async (offerInfo) => {
    const data = {
        title: offerInfo.title,
        offerId: offerInfo.offerId,
        startDate:  offerInfo.startDate,
        endDate:  offerInfo.endDate,
        description:  offerInfo.description,
        offerCode:  offerInfo.offerCode

    }

    const response = await axios.post(
        "http://localhost:8080/BusinessUsers/UpdateOffer/",
        data,
        {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
            },
        },
    )
    .catch((error) => {
        if (error.response) {
            console.log('Server responded with status code:', error.response.status);
            console.log('Response data:', error.response.data);
        } else if (error.request) {
            console.log('No response received:', error.request);
        } else {
            console.log('Error creating request:', error.message);
        }
    });
    return await response.data;
};


// Delete offer
export const deleteOffer = async (offerInfo) => {
    const data = {
         offerId: offerInfo.offerId

    }

    const response = await axios.delete(
        "http://localhost:8080/BusinessUsers/DeleteOffer/",
        {
            data,
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
            },
        },
    )
    .catch((error) => {
        if (error.response) {
            console.log('Server responded with status code:', error.response.status);
            console.log('Response data:', error.response.data);
        } else if (error.request) {
